import React, { Component } from "react";
import { makeStyles } from "@material-ui/core/styles";
import {connect} from "react-redux";
import {
  Grid,
  Paper,
  TableContainer,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  withStyles,
  ButtonGroup,
  Button,
} from "@material-ui/core";
import EditIcon from "@material-ui/icons/Edit";
import DeleteIcon from "@material-ui/icons/Delete";
import { fetchAllCategories } from "../actions/index";
import AddBoxRoundedIcon from '@mate'
import * as ac from '../actions'
//import Button from "@material-ui/core/Button";
//import axios from "axios";


const useStyles = (theme) =>
  makeStyles({
    table: {
      minWidth: 100,
    },
    paper: {
      margin: theme.spacing(4),
      padding: theme.spacing(4),
    },
  });
 class CategoryList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      obj: {},
    };
  }
  componentDidMount(){
           this.props.fetchCategories()
  console.log(this.props.Categories)
  console.log(this.props.fetchCategories)
  }
  GetCategories=()=>{
      console.log(this.props.Categories)
      console.log('some')
  }
  render() {
    const classes = useStyles;
    const categorylist=this.props.Categories
    return (
      <div>
        <TableContainer component={Paper}>
          <Table
            className={classes.table}
            size="small"
            aria-label="a dense table"
          >
            <TableHead>
              <TableRow>
                <TableCell align="center">Category</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {categorylist.map((row) => (
                <TableRow key={row.id}>
                  <TableCell align="center">{row.categoryName}</TableCell>
                  <ButtonGroup variant="text">
                    <Button>
                      <EditIcon color="primary" />
                    </Button>
                    <Button>
                      <DeleteIcon color="secondary" />
                    </Button>
                  </ButtonGroup>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </div>
    );
  }
}
const mapStateToProps = (state) => ({
  Categories: state.CategoryReducer.list,
});
const mapDispatchToProps = (dispatch) => ({
  fetchCategories:()=> dispatch(fetchAllCategories())
  //fetchCategories:ac.fetchAllCategories
});
export default connect(mapStateToProps,mapDispatchToProps)(CategoryList);
