import {
  FETCH_ALL_CATEGORIES,
  FETCH_CATEGORY_BYID,
} from "../constants/action-types";
//  ,
//   DELETE_CATEGORY,
//   CREATE_CATEGORY,
//   UPDATE_CATEGORY,
const initialState = {
  list:null,
  choice:null
};
const CategoryReducer = (state = initialState, action) => {
  switch (action.type) {
    case FETCH_ALL_CATEGORIES:
      return {
        ...state,
        list: action.payload,
      };
    case FETCH_CATEGORY_BYID:
      return {
        ...state,
        choice: action.payload,
      };
    default:
      return state;
  }
};
export default CategoryReducer;
