import React, { Component } from "react";
//import { makeStyles } from "@material-ui/core/styles";
import {connect} from "react-redux";
import {
  Paper,
  TableContainer,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  ButtonGroup,
  Button,
  withStyles
} from "@material-ui/core";
import Fab from "@material-ui/core/Fab";
import EditIcon from "@material-ui/icons/Edit";
import DeleteIcon from "@material-ui/icons/Delete";
import AddIcon from "@material-ui/icons/Add";
import DetailsSharp from "@material-ui/icons/DetailsSharp";
import { fetchAllCategories } from "../actions/index";
import { makeStyles } from "@material-ui/core/styles";
import { Link } from "react-router-dom";
import compose from "recompose/compose";

//import AddBoxRoundedIcon from '@material-ui/icons/AddBoxRoundedIcon';



const useStyles = makeStyles((theme) =>
 ({
    table: {
      minWidth: 100,
    },
    paper: {
      margin: theme.spacing(4),
      padding: theme.spacing(4),
    },
    root: {
      "& > *": {
        margin: theme.spacing(1),
      },
    },
    extendedIcon: {
      marginRight: theme.spacing(1),
    },
  }));
 class CategoryList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      obj: {},
    };
  }
  componentDidMount(){
           this.props.fetchCategories()
          
  }
  
  render() {
     //const classes = useStyles;
      const { classes } = this.props;
    
     const categorylist=this.props.Categories
    return (
      <div className={classes.root}>
          <Link to="/createCategory">
            <Fab color="primary" aria-label="add">
              <AddIcon />
            </Fab>
          </Link>
       
       
        <TableContainer component={Paper}>
          <Table
            className={classes.table}
            size="small"
            aria-label="a dense table"
          >
            <TableHead>
              <TableRow>
                <TableCell align="center">Category</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {categorylist.map((row) => (
                <TableRow key={row.id}>
                  <TableCell align="center">{row.categoryName}</TableCell>
                  <ButtonGroup variant="text">
                    <Button>
                      <EditIcon color="primary" />
                    </Button>
                    <Button>
                      <DeleteIcon color="secondary" />
                    </Button>
                    <Link to={`/Category/${row.id}`}>
                      <Button>
                        <DetailsSharp />
                      </Button>
                    </Link>
                  </ButtonGroup>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </div>
    );
  }
}
const mapStateToProps = (state) => ({
  Categories: state.CategoryReducer.list,
});
const mapDispatchToProps = (dispatch) => ({
  fetchCategories:()=> dispatch(fetchAllCategories())
  //fetchCategories:ac.fetchAllCategories
});
export default withStyles(useStyles)(connect(mapStateToProps,mapDispatchToProps)(CategoryList));
