import axios from "axios";
const CategoryProduct = "https://localhost:44314/api/CategoryProduct/";
//const ProductApi = "https://localhost:44314/api/Product/";
export default {
  apiCategory(url = CategoryProduct) {
    return {
     
      fetchById: (id) => axios.get(url + id),
      create: (category) => axios.post(url, category),
      delete: (id) => axios.delete(url + id),
      edit: (id, category) => axios.put(url + id, category),
    };
  },
};


