import {
  FETCH_ALL_CATEGORIES,
 
} from "../constants/action-types";
import api from './api'
 //FETCH_CATEGORY_BYID,
//   DELETE_CATEGORY,
//   CREATE_CATEGORY,
//   UPDATE_CATEGORY,

export const GetCategories = (cates) => ({
         type: FETCH_ALL_CATEGORIES,
         payload:cates
       });
export const fetchAllCategories=()=>dispatch=>{
    api.apiCategory().fetchAll()
    .then(res=>{
        console.log(res);
        dispatch();
    })
}
