import {
  FETCH_ALL_CATEGORIES,
  FETCH_CATEGORY_BYID,
  DELETE_CATEGORY,
  CREATE_CATEGORY,
  UPDATE_CATEGORY,
} from "../constants/action-types";

const initialState = {
  list: []
};
const CategoryReducer = (state = initialState, action) => {
  switch (action.type) {
    case FETCH_ALL_CATEGORIES:
      return {
        ...state,
        list: [...action.payload],
      };

    default:
      return state;
  }
};
export default CategoryReducer;
