import {
  FETCH_ALL_CATEGORIES,
 
} from "../constants/action-types";
//  FETCH_CATEGORY_BYID,
//   DELETE_CATEGORY,
//   CREATE_CATEGORY,
//   UPDATE_CATEGORY,
const initialState = {
  list: [],
  choice
};
const CategoryReducer = (state = initialState, action) => {
  switch (action.type) {
    case FETCH_ALL_CATEGORIES:
      return {
        ...state,
        list: [...action.payload],
      };

    default:
      return state;
  }
};
export default CategoryReducer;
